def create_user(options)
  if User.exists?(:email => options[:email])
    puts "Ignoring - User already exists for: #{options[:email]}"
  else
    user = User.create!(options)
    puts "Successfully created User: #{options[:email]}"
  end
end

def create_boodle(options)
  if Boodle.exists?(title: options[:title], user: options[:user_id])
    puts "Ignoring - Boodle already exists for: #{options[:title]}"
  else
    user = Boodle.create!(options)
    puts "Successfully created Boodle: #{options[:title]}"
  end
end

def create_user_type(options)
  if UserType.exists?(name: options[:name])
    puts "Ignoring - UserType already exists for: #{options[:name]}"
  else
    ut = UserType.create!(options)
    puts "Succesfully create UserType: #{options[:name]}"
  end
end

def create_story_part(options)
  name = "#{options[:category]}: #{options[:description]}"
  if StoryPart.exists?(category: options[:category], description: options[:description])
    puts "Ignoring - StoryPart already exists for: #{name}"
  else
    sp = StoryPart.create!(options)
    puts "Succesfully create StoryPart: #{name}"
  end
end
